sql.select:
		select t2.*,t3.id comment_id,t3.remark,t3.gmt_remark,t4.name remark_name  from
		(
        select
            t1.*
        from
            view_apply t1
        where
            1 = 1
            {{id | and:'t1.id'}}
            {{user_id | and:'t1.user_id'}}
            {{pid | and:'t1.pid'}}
            {{state | in:'t1.state'}}
            {{auth_detail | and:'t1.auth_detail'}}
            {{key | and:'t1.user_name,t1.content,t1.reason','like'}}
            {{user_id | and:'t1.user_id'}}
            {{department_id | and:'t1.department_id'}}
            {{sort | orderby}}
            {{offset | limit:pageSize}}
		) t2
		left join
			comment t3 on t2.id = t3.apply_id
		left join
			user t4 on t3.user_id = t4.id
		order by t2.id asc,t3.id asc

select
    count(1)
from
    view_apply t1
where
    1 = 1
    {{id | and:'t1.id'}}
    {{user_id | and:'t1.user_id'}}
    {{pid | and:'t1.pid'}}
    {{state | in:'t1.state'}}
    {{auth_detail | and:'t1.auth_detail'}}
    {{key | and:'t1.user_name,t1.content,t1.reason','like'}}
    {{user_id | and:'t1.user_id'}}
    {{department_id | and:'t1.department_id'}}
    {{user_id | and:'t1.user_id'}}
