sql.update:
update
    apply_type
set
    {{name | set:'name'}}
    {{pid | set:'pid'}}
where
    id = {{id | q}}

sql.select:
select
    sum(total)
from(
    (select count(1) total from apply_type where pid = {{id | q}} limit 1)
    union
    (select count(1) total from apply where auth_detail = {{id | q}} limit 1)
) s;
